let getStatus = null;
let getConfig = null;
let getPos = null;
let setReset = null;
let setRunFWUpgrade = null;
let setLat = null;
let setLon = null;
let setMAN = null;
let setAUTO = null;
let planleggerTabell = null;


// Command (cmd)
if ((msg.payload.cmd === "Status") || (msg.payload.cmd === "status") || (msg.payload.cmd === "STATUS")) {
    getStatus = true;
} else if ((msg.payload.cmd === "Konfig") || (msg.payload.cmd === "konfig") || (msg.payload.cmd === "KONFIG")
    || (msg.payload.cmd === "Config") || (msg.payload.cmd === "config") || (msg.payload.cmd === "CONFIG")) {
    getConfig = true;
} else if ((msg.payload.cmd === "Pos") || (msg.payload.cmd === "pos") || (msg.payload.cmd === "POS")
    || (msg.payload.cmd === "gps") || (msg.payload.cmd === "GPS")) {
    getPos = true;
} else if ((msg.payload.cmd === "Reset") || (msg.payload.cmd === "reset") || (msg.payload.cmd === "RESET")) {
    setReset = true;
} else if ((msg.payload.cmd === "RunFWUpgrade") || (msg.payload.cmd === "runfwupgrade") || (msg.payload.cmd === "Run FW upgrade") || (msg.payload.cmd === "RUNFWUPGRADE")) {
    setRunFWUpgrade = true;
} else if ((msg.payload.cmd === "MAN") || (msg.payload.cmd === "man") || (msg.payload.cmd === "Manuell") || (msg.payload.cmd === "manuell") ) {
    setMAN = true;
} else if ((msg.payload.cmd === "AUTO") || (msg.payload.cmd === "auto") || (msg.payload.cmd === "Auto") ) {
    setAUTO = true;
}


// Lat
if (msg.payload.Lat !== undefined) {
    setLat = msg.payload.Lat;
}

// Lon
if (msg.payload.Lon !== undefined) {
    setLon = msg.payload.Lon;
}

// planlegger Tabell
if (msg.payload.planleggerTabell !== undefined) {
    if (Array.isArray(msg.payload.planleggerTabell)) {
        if ( (msg.payload.planleggerTabell.length === 0)
          || ( (msg.payload.planleggerTabell[0].Tid !== undefined) 
            && (msg.payload.planleggerTabell[0].Lys !== undefined) 
            && (msg.payload.planleggerTabell[0].Lamper !== undefined) ) 
        ) {
            planleggerTabell = msg.payload.planleggerTabell;
        }
    }
}

return [
    ( getStatus === null ? null : { payload: getStatus } ),
    ( getConfig === null ? null : { payload: getConfig } ),
    ( getPos === null ? null : { payload: getPos } ),
    ( setReset === null ? null : { payload: setReset } ),
    ( setRunFWUpgrade === null ? null : { payload: setRunFWUpgrade } ),
    ( msg.payload.Stigetid === undefined ? null : { payload: msg.payload.Stigetid } ),
    ( msg.payload.Dimmetid === undefined ? null : { payload: msg.payload.Dimmetid } ),
    ( msg.payload.MaxDriverTemperatur === undefined ? null : { payload: msg.payload.MaxDriverTemperatur } ),
    ( msg.payload.DriverCoolDownPWM === undefined ? null : { payload: msg.payload.DriverCoolDownPWM } ),
    ( setLat === null ? null : { payload: setLat } ),
    ( setLon === null ? null : { payload: setLon } ),
    ( setMAN === null ? null : { payload: 0 } ),
    ( setAUTO === null ? null : { payload: 1 } ),
    ( planleggerTabell === null ? null : { payload: planleggerTabell } )
];

