];

let Lampe = null;
let setPWM = null;
let setEnable = null;
let setMBID = null;
let setREP0E = null;
let sendMB6 = null;


// Lampe?
if ((msg.payload.Lampe !== undefined) && (msg.payload.Lampe >= 0) && (msg.payload.Lampe <= 6)) {
    Lampe = msg.payload.Lampe;
    if ((msg.payload.setPWM !== undefined) && (msg.payload.setPWM >= 0) && (msg.payload.setPWM <= 10000)) {
        setPWM = msg.payload.setPWM;
    }
    if (msg.payload.Enable !== undefined) {
        setEnable = msg.payload.Enable;
    }
    if (msg.payload.enable !== undefined) {
        setEnable = msg.payload.enable;
    }
    if (msg.payload.ENABLE !== undefined) {
        setEnable = msg.payload.ENABLE;
    }
    // payload: { "value": msg.payload, "fc": 6, "unitid": 11, "address": 3, "quantity": 1 }
    if ( (msg.payload.Lampe > 0) && (msg.payload.value !== undefined) && (msg.payload.fc !== undefined) && (msg.payload.address !== undefined) ) {
        if((msg.payload.value >= 0) && (msg.payload.value <= 65535) && (msg.payload.fc == 6) && (msg.payload.address > 0) && (msg.payload.address < 250) ) {
            sendMB6 = msg.payload.fc;
        }
    }

}

// MODBUS ID
if (msg.payload.MBID !== undefined) {
  if ((msg.payload.MBID > 10) && (msg.payload.MBID < 17) ) {
    setMBID = msg.payload.MBID;
  }
}

// REP0E - Repair 0-error
if (msg.payload.REP0E !== undefined) {
  if ((msg.payload.REP0E > 10) && (msg.payload.REP0E < 17) ) {
    setREP0E = msg.payload.REP0E;
  }
}

return [
    ( setPWM === null ? null : { payload: { "Lampe": Lampe, "setPWM": setPWM } }),
    ( setEnable === null ? null : { payload: { "Lampe": Lampe, "Enable": setEnable } } ),
    ( setMBID === null ? null : { payload: setMBID } ),
    ( sendMB6 === null ? null : { payload: { "value": msg.payload.value, "fc": 6, "unitid": 10+msg.payload.Lampe, "address": msg.payload.address, "quantity": 1 } } ),
    ( setREP0E === null ? null : { payload: setREP0E } )
];
