/*
Tower Status Summer
*/
function myStatusSummer(status, newStatus) {

    // Off?
    if (status == "ÿ") {
        return newStatus;
    }

    // Alarm?
    if(newStatus == "r") {
            return "r";
    }
    if ((newStatus == "R") || (status == "R") || (status == "r")) {
        if ((status == "R") || (status == "r")) {
            return "r";
        } else {
            return "R";
        }
    }

    // Warning?
    if (newStatus == "y") {
        return "y";
    }
    if ((newStatus == "Y") || (status == "Y") || (status == "y")) {
        if ((status == "Y") || (status == "y")) {
            return "y";
        } else {
            return "Y";
        }
    }

    // OK/in progress?
    if ((newStatus == "g") || (status == "g"))  {
        return "g";
    }
    if (newStatus == "G") {
        return "G";
    }

    // Disabled/Off?
    if(newStatus == "ÿ") {
        return status;  // return system Status unchanges
    }

    return "ÿ";
}

